mapper.select:
select
  id, `name`, `desc`, exp, create_time, update_time, create_by, involve, is_delete, field_type, data_type
from datalabel


        and id = #{id}


        and `name` = #{name}


        and `desc` = #{desc}


        and exp = #{exp}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and create_by = #{createBy}


        and involve = #{involve}


        and is_delete = #{isDelete}


        and data_type = #{dataType}


        and field_type = #{fieldType}

    and is_delete=0

limit #{pageable.offset}, #{pageable.pageSize}

select id, `name`, `desc`,`expression`, exp, create_time, update_time, create_by, involve, is_delete, field_type, data_type from datalabel
and name like concat('%',#{keyWord},'%')
and create_time >= #{time} and create_time < #{plusOneTime}
and create_by = #{userId} and is_delete=0
limit #{pageNo}, #{pageSize}

select
    id, `name`, `desc`,exp
from datalabel where is_delete=0 and create_by = #{createBy}

