mapper.select:
select
  id, name, create_by, create_time, update_time, `desc`
from datasource_group


        and id = #{id}


        and name = #{name}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_time = #{updateTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from datasource_group


        and id = #{id}


        and name = #{name}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_time = #{updateTime}

select id, name, `desc`
from datasource_group


        and create_by = #{createBy}
